import { createIcon } from "./Icon";

const path = (
  d: string,
  opts?: { fillRule?: "evenodd"; clipRule?: "evenodd" },
) => (
  <path
    d={d}
    fill="currentColor"
    {...(opts?.fillRule && { fillRule: opts.fillRule })}
    {...(opts?.clipRule && { clipRule: opts.clipRule })}
  />
);

const strokePath = (d: string) => (
  <path
    d={d}
    fill="none"
    stroke="currentColor"
    strokeWidth={1.8}
    strokeLinecap="round"
    strokeLinejoin="round"
  />
);

